SELECT s.id, s.scenario_id, s.agent_id, s.scenario_code, s.scenario_name, s.scenario_type, s.trigger_type, s.trigger_keywords, s.trigger_cards, s.description, s.difficulty_level, s.target_age, s.sort_order, s.is_active, s.is_default_teaching, s.teaching_mode_config, s.auto_switch_to_free, s.max_user_replies, s.praise_messages, s.encouragement_messages, s.creator, s.create_date, s.updater, s.update_date, COALESCE(COUNT(st.id), 0) as step_count, a.agent_name FROM ai_scenario s LEFT JOIN ai_scenario_step st ON s.scenario_id = st.scenario_id COLLATE utf8mb4_unicode_ci LEFT JOIN ai_agent a ON s.agent_id = a.id COLLATE utf8mb4_unicode_ci
AND s.agent_id = #{agentId}
AND s.scenario_type = #{scenarioType}
AND s.is_active = #{isActive}
AND s.scenario_name LIKE CONCAT('%', #{scenarioName}, '%')
GROUP BY s.id, s.scenario_id, s.agent_id, s.scenario_code, s.scenario_name, s.scenario_type, s.trigger_type, s.trigger_keywords, s.trigger_cards, s.description, s.difficulty_level, s.target_age, s.sort_order, s.is_active, s.is_default_teaching, s.teaching_mode_config, s.auto_switch_to_free, s.max_user_replies, s.praise_messages, s.encouragement_messages, s.creator, s.create_date, s.updater, s.update_date, a.agent_name ORDER BY s.sort_order ASC, s.create_date DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(DISTINCT s.id)
FROM ai_scenario s
LEFT JOIN ai_scenario_step st ON s.scenario_id = st.scenario_id COLLATE utf8mb4_unicode_ci
LEFT JOIN ai_agent a ON s.agent_id = a.id COLLATE utf8mb4_unicode_ci


        AND s.agent_id = #{agentId}


        AND s.scenario_type = #{scenarioType}


        AND s.is_active = #{isActive}


        AND s.scenario_name LIKE CONCAT('%', #{scenarioName}, '%')

SELECT 

FROM ai_scenario
WHERE scenario_type = #{scenarioType}
AND is_active = #{isActive}
ORDER BY sort_order ASC, create_date DESC
